CREATE TABLE `tbl_policy` (
  `policy_id` int(100) NOT NULL,
  `policy_title` varchar(100) NOT NULL,
  `policy_desc` varchar(500) NOT NULL,
  `policy_content` text NOT NULL,
  `status_current` enum('publish','pending','trash') NOT NULL DEFAULT 'pending',
  `status_old` enum('publish','pending','trash') DEFAULT NULL,
  `created_date` int(100) NOT NULL,
  `update_date` int(100) DEFAULT NULL,
  `creator_id` int(50) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

